DROP TABLE IF EXISTS `m_post`;

CREATE TABLE `m_post` (
                          `id` int(11) NOT NULL AUTO_INCREMENT,
                          `post_desc` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
                          `cat_id` int(3) DEFAULT NULL,
                          `rank_id` int(3) DEFAULT NULL,
                          `status` int(1) DEFAULT 1,
                          PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=227 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

